import React, { useEffect, useState } from 'react'
import axios from 'axios';
import Dashboard from './Dashboard';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();

  const [username,setUserName] = useState<String>('')
  const [password,setPassword] = useState<Number | String>('')


  async function loginHandler() {
    const item = { username, password };
    try {
      const response = await axios.post('https://logintesting.p.rapidapi.com/login', item, {
        headers: {
          'content-type': 'application/json',
          Authentication: '5hvsyf2K6xuUfJVxRu5mDaNI0IAODh',
          'X-RapidAPI-Key': '1bdf2179a3mshd1b2efa8a3783b2p14e990jsn56a9af198b87',
          'X-RapidAPI-Host': 'logintesting.p.rapidapi.com',
        },
      });

      const result = response.data;
      localStorage.setItem('user info', JSON.stringify(result.data));
      if (localStorage.getItem('user info')) {
        // Redirect to Dashboard if access token is present
        navigate('/dashboard');
      }
      } catch (error) {
      // Handle errors here, e.g., display an error message or redirect to an error page.
      console.error('Error occurred during login:', error);
    }
  }

  return (
    <div>
    <h1> Login!</h1>
    <input type = 'text' placeholder='userName' onChange={(e)=> setUserName(e.target.value)} />
    <input type = 'password' placeholder='password' onChange={(e)=>setPassword(e.target.value)} />
    <button onClick={loginHandler}>login</button>
  </div>
  )
}

export default Login
